import React,{useRef} from 'react';
import './contact.css';
import emailjs from "@emailjs/browser";

function Contact() {
    const openInNewTab = (url) => {
        window.open(url,"_blank", "noreferrer")
    }

    const form = useRef();

    const sendEmail = (e) => {
        e.preventDefault();

        console.log(form.current)
    }



    return (
        <div>
            <div color='blue' className="container border"
                style={{
                    marginTop: "50px",
                width:'50%'}}>
                <form className="--form-control --card --flex-center --dir-column" onSubmit={sendEmail}>
                    <label for="user_name" >Name</label>
                    <input type="text" placeholder='Full Name' name="user_name" required />
                    <label for="user_email">Email</label>
                    <input type="email" placeholder="Email" name="user_email" required/>
                    <label>Message</label>
                    <textarea name="message" cols="30" rows="10"/>
                    <input type="submit" value="Send" />
                </form>
            </div>


        
        <div className='page'>

            <div className='button-group'>
                <button className='LinkedIn' onClick={()=>openInNewTab("https://www.linkedin.com/in/akash-sivapalan-309a85186/")}>
                <i class="fa-brands fa-linkedin fa-2xl" ></i>
                </button>
                <button className='GitHub'  onClick={()=>openInNewTab("https://github.com/AkashSivapalan")}>
                    <i class="fa-brands fa-github fa-2xl"  ></i>
                </button>
            </div>

            </div>
        </div>);
}

export default Contact;
