import React, { useRef } from "react";
import "./contact.css";
import emailjs from "@emailjs/browser";
import styled from "styled-components";
function Contact() {
  const openInNewTab = (url) => {
    window.open(url, "_blank", "noreferrer");
  };

  const form = useRef();

  const sendEmail = (e) => {
    e.preventDefault();

    console.log(form.current);
  };

  return (
    <div>
      <div className="page">
        <p className="mail">
          My inbox is open if you are interested in chatting about latest news
          in tech or disucss project oppurutnities. My email is:
        </p>
        <p className="mail">
          <i class="fa fa-envelope" aria-hidden="true"></i>
          akashsivapalan@gmail.com
        </p>
        <br />
        <p className="mail">Below are some other socials to get to know me</p>

        <div>
          <form ref={form} onSubmit={sendEmail}>
            <label>Name</label>
            <input type="text" name="user_name" />
            <label>Email</label>
            <input type="email" name="user_email" />
            <label>Message</label>
            <textarea name="message" />
            <input type="submit" value="Send" />
          </form>
        </div>

        <div className="button-group">
          <button
            className="LinkedIn"
            onClick={() =>
              openInNewTab(
                "https://www.linkedin.com/in/akash-sivapalan-309a85186/"
              )
            }
          >
            <i class="fa-brands fa-linkedin fa-2xl"></i>
          </button>
          <button
            className="GitHub"
            onClick={() => openInNewTab("https://github.com/AkashSivapalan")}
          >
            <i class="fa-brands fa-github fa-2xl"></i>
          </button>
        </div>
      </div>
    </div>
  );
}

export default Contact;
