import React,{useRef} from 'react';
import './contact.css';
import emailjs from "@emailjs/browser";

function Contact() {
    const openInNewTab = (url) => {
        window.open(url,"_blank", "noreferrer")
    }

    const form = useRef();

    const sendEmail = (e) => {
        e.preventDefault();

        console.log(form["user_name"])
    }



    return (
        <div className='page'>
            <div className='EmailForm'>
                <form className='--form-control' onSubmit={sendEmail}>
                    <label for="user_name" >Name</label>
                    <input type="text" placeholder='Full Name' name="user_name" />
                    <label for="user_email">Email</label>
                    <input type="email" name="user_email" />
                    <label>Message</label>
                    <textarea name="message" />
                    <input type="submit" value="Send" />
                </form>
            </div>
            <div className='button-group'>
                <button className='LinkedIn' onClick={()=>openInNewTab("https://www.linkedin.com/in/akash-sivapalan-309a85186/")}>
                <i class="fa-brands fa-linkedin fa-2xl" ></i>
                </button>
                <button className='GitHub'  onClick={()=>openInNewTab("https://github.com/AkashSivapalan")}>
                    <i class="fa-brands fa-github fa-2xl"  ></i>
                </button>
            </div>

        </div>  );
}

export default Contact;
